mapper.select:
SELECT * FROM posts
WHERE author_id = #{authorId}
ORDER BY post_publish_time DESC

mapper.update:
UPDATE posts
SET likes_num = likes_num + 1
WHERE post_id = #{postId}

UPDATE posts
SET likes_num = likes_num - 1
WHERE post_id = #{postId}

UPDATE posts
SET comments_num = comments_num + 1
WHERE post_id = #{postId}

UPDATE posts
SET comments_num = comments_num - 1
WHERE post_id = #{postId}
